// Transform SSG course catalog response into ingestion schema
const rawItems = items[0].json;
const courses = [];

if (rawItems && rawItems.courses) {
  // Seed data format: already matches our schema
  for (const c of rawItems.courses) {
    courses.push({
      title: c.title,
      provider: c.provider || 'Unknown',
      skills_taught: c.skills_taught || c.skillsTaught || [],
      duration_weeks: c.duration_weeks || c.durationWeeks,
      level: c.level || 'intermediate',
      url: c.url,
      certification: c.certification,
      course_fee: c.course_fee || c.courseFee || 0,
      subsidy_percent: c.subsidy_percent || c.subsidyPercent || 70,
      mces_eligible: c.mces_eligible || c.mcesEligible || false,
      skillsfuture_eligible: c.skillsfuture_eligible !== undefined ? c.skillsfuture_eligible : true,
      skillsfuture_credit_amount: c.skillsfuture_credit_amount || 500.0,
      nett_fee_after_subsidy: c.nett_fee_after_subsidy || 500.0
    });
  }
} else if (Array.isArray(rawItems)) {
  // SSG API format (adapt when live)
  for (const item of rawItems) {
    courses.push({
      title: item.courseTitle || item.title,
      provider: item.trainingProvider || item.provider || 'Unknown',
      skills_taught: item.skills || [],
      duration_weeks: Math.ceil((item.totalDurationHours || 40) / 40),
      level: item.courseLevel || 'intermediate',
      url: item.courseUrl || null,
      certification: item.certificationName || null,
      course_fee: item.totalFee || 0,
      subsidy_percent: item.subsidyRate || 70,
      mces_eligible: item.mcesEligible || false,
      skillsfuture_eligible: true,
      skillsfuture_credit_amount: 500.0,
      nett_fee_after_subsidy: item.nettFee || 500.0
    });
  }
}

return [{ json: { courses } }];
